mapper.select:
        select
id, name, storage, goodsType, count, remark
        from goods


                and id = #{id}


                and name = #{name}


                and storage = #{storage}


                and goodsType = #{goodstype}


                and count = #{count}


                and remark = #{remark}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from goods


        and id = #{id}


        and name = #{name}


        and storage = #{storage}


        and goodsType = #{goodstype}


        and count = #{count}


