mapper.select:
select
id, goods, userId, admin_id, count, createtime, remark
from record


        and id = #{id}


        and goods = #{goods}


        and userId = #{userid}


        and admin_id = #{adminId}


        and count = #{count}


        and createtime = #{createtime}


        and remark = #{remark}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from record


        and id = #{id}


        and goods = #{goods}


        and userId = #{userid}


        and admin_id = #{adminId}


        and count = #{count}


        and createtime = #{createtime}


        and remark = #{remark}
