mapper.select:
        select
id, title, content, created_at, updated_at
        from richtext


                and id = #{id}


                and title = #{title}


                and content = #{content}


                and created_at = #{createdAt}


                and updated_at = #{updatedAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from richtext


        and id = #{id}


        and title = #{title}


        and content = #{content}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
