mapper.select:
        select
id, name, content, created_at, updated_at
        from text


                and id = #{id}


                and name = #{name}


                and content = #{content}


                and created_at = #{createdAt}


                and updated_at = #{updatedAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from text


        and id = #{id}


        and name = #{name}


        and content = #{content}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
